function decodeFile(nodeName, parseJson=false) {
  const node = $node[nodeName].json;
  if (!node.content) throw new Error(`${nodeName} has no json.content`);
  const buff = Buffer.from(node.content, 'base64');
  const text = buff.toString('utf-8');
  return parseJson ? JSON.parse(text) : text;
}

const db = decodeFile("Get content-database.json", true);
const guidelinesText = decodeFile("Get Guidelines", false);
const { topic } = $node["Set Post Topic"].json;

// Convert database to compact format: key|slug|title
// This reduces token usage by ~80% while preserving all necessary data
function formatDbCompact(dbObj, category) {
  return Object.entries(dbObj || {})
    .map(([key, val]) => {
      // Prepend category prefix to slug (e.g., /crypto-ogs/, /posts/, /exchanges/, /tools/)
      const fullSlug = `/${category}/${val.slug}`;
      return [key, fullSlug, val.title].join('|');
    })
    .join('\n');
}

const compactOgs = formatDbCompact(db.crypto_ogs, 'crypto-ogs');
const compactPosts = formatDbCompact(db.posts, 'posts');
const compactExchanges = formatDbCompact(db.exchanges, 'exchanges');
const compactTools = formatDbCompact(db.tools, 'tools');

return [{
  json: { 
    db,              // Full database for backend processing
    guidelinesText,
    topic, 
    compactOgs,      // Compact format for AI (80% fewer tokens)
    compactPosts,
    compactExchanges,
    compactTools
  }
}];

