parameters.functionCode:
const meta = $node["Parse & Prepare Data"].json;
const ai = $node["OpenAI: Generate Body"].json;

// Generate slug from topic
let slug = meta.topic.toLowerCase()
  .replace(/^what is /i, '')
  .replace(/^how to /i, '')
  .replace(/^understanding /i, '')
  .replace(/[^a-z0-9]+/g, '-')
  .replace(/(^-|-$)/g, '');

const isoDate = new Date().toISOString().split('T')[0]; // YYYY-MM-DD format
const order = meta.db.next_orders.posts;

// Extract metadata from AI response
let tags = [];
let description = '';
let categories = [];
let cryptoOgs = [];
let exchanges = [];
let jsonFound = false;

try {
  const aiContent = ai.choices?.[0]?.message?.content || ai.content || '';
  console.log('AI Response length:', aiContent.length);

  // Look for JSON object in the response
  const jsonMatch = aiContent.match(/\{[\s\S]*?"tags"[\s\S]*?\}/g);
  if (jsonMatch && jsonMatch.length > 0) {
    console.log('Found JSON match:', jsonMatch[0].substring(0, 200));

    // Clean up the JSON string before parsing
    let jsonString = jsonMatch[0];

    // Fix common JSON issues
    jsonString = jsonString
      .replace(/,(\s*[}\]])/g, '$1') // Remove trailing commas
      .replace(/([^,{}\s])(\s*[}\]])/g, '$1$2') // Ensure proper comma placement
      .replace(/([^,{}\s])(\s*"[a-zA-Z_][a-zA-Z0-9_]*"\s*:)/g, '$1,$2'); // Add missing commas before properties

    try {
      const aiData = JSON.parse(jsonString);

      // Extract all metadata
      if (aiData.tags && Array.isArray(aiData.tags)) {
        tags = aiData.tags;
        console.log('Extracted tags:', tags.length);
      }

      if (aiData.description) {
        description = aiData.description;
        console.log('Extracted description:', description);
      }

      if (aiData.categories && Array.isArray(aiData.categories)) {
        categories = aiData.categories;
        console.log('Extracted categories:', categories);
      }

      if (aiData.crypto_ogs && Array.isArray(aiData.crypto_ogs)) {
        cryptoOgs = aiData.crypto_ogs;
        console.log('Extracted crypto_ogs:', cryptoOgs);
      }

      if (aiData.exchanges && Array.isArray(aiData.exchanges)) {
        exchanges = aiData.exchanges;
        console.log('Extracted exchanges:', exchanges);
      }

      jsonFound = true;
    } catch (jsonError) {
      console.log('JSON parsing failed, trying manual extraction:', jsonError.message);

      // Manual extraction as fallback
      const tagsMatch = aiContent.match(/"tags"\s*:\s*\[([^\]]+)\]/);
      const descMatch = aiContent.match(/"description"\s*:\s*"([^"]+)"/);
      const catsMatch = aiContent.match(/"categories"\s*:\s*\[([^\]]+)\]/);
      const ogsMatch = aiContent.match(/"crypto_ogs"\s*:\s*\[([^\]]+)\]/);
      const exMatch = aiContent.match(/"exchanges"\s*:\s*\[([^\]]+)\]/);

      if (tagsMatch) {
        tags = tagsMatch[1].split(',').map(t => t.trim().replace(/["']/g, ''));
      }

      if (descMatch) {
        description = descMatch[1];
      }

      if (catsMatch) {
        categories = catsMatch[1].split(',').map(c => c.trim().replace(/["']/g, ''));
      }

      if (ogsMatch) {
        cryptoOgs = ogsMatch[1].split(',').map(o => o.trim().replace(/["']/g, ''));
      }

      if (exMatch) {
        exchanges = exMatch[1].split(',').map(e => e.trim().replace(/["']/g, ''));
      }

      if (tags.length > 0 || description) {
        jsonFound = true;
        console.log('Manual extraction successful');
      }
    }
  }

  // Validate we got the required data
  if (!jsonFound || !description) {
    throw new Error('AI did not return required JSON with tags and description. Manual review needed.');
  }

  if (tags.length < 8) {
    console.log(`Warning: Only ${tags.length} tags found. Minimum 8 recommended.`);
  }

  if (categories.length === 0) {
    console.log('Warning: No categories found. Using default.');
    categories = ['Beginners'];
  }

  // Validate categories against allowed list
  const allowedCategories = ['Investing', 'Beginners', 'Regulation', 'Bitcoin', 'Adoption', 'Blockchain', 'Technology', 'Web3', 'Predictions', 'Security', 'Politics', 'Sustainability', 'Gaming', 'Nfts', 'Ai'];
  const invalidCategories = categories.filter(cat => !allowedCategories.includes(cat));
  if (invalidCategories.length > 0) {
    console.log(`Warning: Invalid categories found: ${invalidCategories.join(', ')}. Removing them.`);
    categories = categories.filter(cat => allowedCategories.includes(cat));
  }
  if (categories.length === 0) {
    console.log('No valid categories remaining. Using default.');
    categories = ['Beginners'];
  }

} catch (e) {
  console.log('ERROR parsing AI response:', e.message);
  throw new Error(`Failed to extract data from AI response: ${e.message}. Please check AI output format.`);
}

// Build frontmatter
const frontmatterLines = [
  '---',
  `title: "${meta.topic}"`,
  `description: "${description}"`,
  `date: ${isoDate}`,
  `image: "/images/posts/${slug}.png"`,
  `categories: [${categories.map(c => `"${c}"`).join(', ')}]`
];

// Add optional crypto-ogs
if (cryptoOgs.length > 0) {
  frontmatterLines.push(`crypto-ogs: [${cryptoOgs.map(og => `"${og}"`).join(', ')}]`);
}

// Add optional exchanges
if (exchanges.length > 0) {
  frontmatterLines.push(`exchanges: [${exchanges.map(ex => `"${ex}"`).join(', ')}]`);
}

frontmatterLines.push('authors: ["Crypto Wiki Team"]');
frontmatterLines.push(`tags:`);
frontmatterLines.push('  [');
tags.forEach((tag, index) => {
  const comma = index < tags.length - 1 ? ',' : '';
  frontmatterLines.push(`    "${tag}"${comma}`);
});
frontmatterLines.push('  ]');
frontmatterLines.push('draft: false');
frontmatterLines.push('---');

const frontmatter = frontmatterLines.join('\n');

// Extract just the markdown body (remove any JSON at the end)
let body = ai.choices?.[0]?.message?.content || ai.content || '';
// Remove JSON object if it exists at the end
body = body.replace(/\n\{[\s\S]*"tags"[\s\S]*\}$/, '').trim();

return [{
  json: {
    mdx: `${frontmatter}\n\n${body}`,
    slug,
    topic: meta.topic,
    isoDate,
    order,
    db: meta.db,
    tags,
    description,
    categories,
    cryptoOgs,
    exchanges
  }
}];

const data = $json;
const filePath = `${data.slug}.mdx`;
const commitMessage = `New post: ${data.topic}`;

return [{
  json: {
    ...data,
    filePath,
    commitMessage
  }
}];

// Validate file write was successful
const fileResult = $input.first().json;
if (!fileResult || fileResult.error) {
  throw new Error(`File write failed: ${fileResult?.error || 'Unknown error'}`);
}

// Get post data from Build Frontmatter + MDX node
const postData = $node["Build Frontmatter + MDX"].json;
if (!postData || !postData.topic || !postData.slug) {
  throw new Error('Post data not found or incomplete');
}

// Get and validate database data
const dbData = postData.db;
if (!dbData) {
  throw new Error('Database data not found');
}

// Create updated database with proper structure
const updatedDb = {
  ...dbData,
  posts: { ...(dbData.posts || {}) },
  next_orders: { ...(dbData.next_orders || { posts: 1 }) }
};

// Create post key (lowercase, replace spaces with dots)
const postKey = postData.slug.replace(/-/g, '.');

// Check if post already exists
if (updatedDb.posts[postKey]) {
  throw new Error(`Post '${postData.topic}' already exists in database`);
}

// Add new post to database (slug without /posts/ prefix)
updatedDb.posts[postKey] = {
  slug: postData.slug,
  title: postData.topic
};

// Increment next order counter
updatedDb.next_orders.posts = (updatedDb.next_orders.posts || 0) + 1;

// Return success with updated database
return [{ 
  json: { 
    db: updatedDb, 
    fileSuccess: true, 
    dbSuccess: true,
    postAdded: postKey,
    topic: postData.topic
  } 
}];
